void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // SECTION A

    float x = fragCoord.x * 0.1;
    float y = (iResolution.y - fragCoord.y) * 0.1;

    float u = x - 36.;
    float v = 18.0 - y;
    float h = (u * u) + (v * v);

    float R = 0.;
    float G = 0.;
    float B = 0.;

    if (h < 200.) {
        // SECTION B
        R = 420.;
        B = 520.;

        float t = 5000. + (8. * h);
        float p = (t * u) / 100.;
        float q = (t * v) / 100.;
        float s = 2. * q;
        float w = ((1000. + p - s) / 100.) + 8.;

        if (w > 0.) {
            R = R + (w * w);
        }

        float o = s + 2200.;

        R = (R * o) / 10000.;
        B = (B * o) / 10000.;

        if (p > -q) {
            float w = (p + q) / 10.;

            R = R + w;
            B = B + w;
        }

    } else if (v < 0.) {
        // SECTION C
        R = 150. + (2. * v);
        B = 50.;

        float p = h + (8. * v * v);
        float c = (240. * (-v)) - p;

        if (c > 1200.) {
            float o = (6. * c) / 10.;
            o = c * (1500. - o);
            o = (o / 100.) - 8360.;

            R = (R * o) / 1000.;
            B = (B * o) / 1000.;
        }

        float r = c + (u * v);
        float d = 3200. - h - (2. * r);

        if (d > 0.) {
            R = R + d;
        }

    } else {
        // SECTION D
        float c = x + (4. * y);
        R = 132. + c;
        B = 192. + c;
    }

    // SECTION E
    if (R > 255.) {
        R = 255.;
    }

    if (B > 255.) {
        B = 255.;
    }

    G = ((7. * R) + (3. * B)) / 10.;

    // Output to screen
    fragColor = vec4(R / 255., G / 255., B / 255., 1.0);
}
