//graphs f(x), "t" is time.
#define function1 x-cos(t)
#define function2 y-sin(t)
#define function3 length(p)-1.

#define zoom 64.
#define stepp 64
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
vec2 p = (fragCoord-iResolution.xy/2.+0.5)/zoom;
float t=iTime;
float x=p.x;
float y=p.y;
float func1,func2,func3;
vec3 rgb;
fragColor=vec4(0);
for (int i=0;i<stepp;i++){
rgb = texture(iChannel0,vec3(float(iFrame)+float(i),p*zoom)/32.).rgb;
func1+=(function1);
func2+=(function2);
func3+=(function3);
fragColor += vec4(vec3(
(abs(mix(func1,func2,rgb.r))-1.)<0.,
(abs(mix(func2,func3,rgb.g))-1.)<0.,
(abs(mix(func3,func1,rgb.b))-1.)<0.

),0.);}
fragColor /= float(stepp);}
