void mainImage( out vec4 O, vec2 F ) {

    vec2   W = iResolution.xy,
           a = (F+F-W) / W.y;

    float  v = length(tanh(a)),
           e ,
           s = iTime;

    // audio ampli
    float audioAmplitude = texture(iChannel0, vec2(0.1)).r * 1.5; 

    for (O *= e; e++ < 8.;)
        O += pow(.008/abs(cos(length( a = fract(a +- tanh(s* .5) * .01 - .5) - .5)
            - tanh(length(a)+-v)*10. + s*e*.25) / 3. - smoothstep (0., .4, v - .8)), 1.1) * (cos(v*6.+.5*e-s+vec4(0,1,9,1))+1.);

    // audio modulation
    O.rgb *= audioAmplitude;

    // fluid movement
    float fluidMovement = sin(iTime * 0.2) * audioAmplitude * 0.03;
    O.rgb += fluidMovement;
}
