float fontWeight = .3;
float smoothness = .005;


float line(vec2 p, vec2 a, vec2 b, float linewidth, float smoothness) {
    vec2 v = a, w = b;   
    float l2 = pow(distance(w, v), 2.);
    if(l2 == 0.0) return distance(p, v);
    float t = clamp(dot(p - v, w - v) / l2, 0., 1.);
    vec2 j = v + t * (w - v);
    float dis = distance(p, j);
    return smoothstep(.05 + linewidth / 100., .05 + smoothness + linewidth / 100., dis);
}


float AA(vec2 uv, float fontWeight) {
    float c = line(uv, vec2(.3, .2), vec2(.5, .75), fontWeight, smoothness);
    c = min(c, line(uv, vec2(.7, .2), vec2(.5, .75), fontWeight, smoothness));
    c = min(c, line(uv, vec2(.38, .4), vec2(.62, .4), fontWeight, smoothness));
    float top = line(uv, vec2(.0, .8), vec2(1., .8), fontWeight, smoothness);
    float bottom = line(uv, vec2(.0, .18), vec2(1., .18), fontWeight, smoothness);
    c = max(1.-top, c);
    return max(1.-bottom, c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //float fontWeight = (.5 + (sin(iTime) *.5)); 
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float AR = iResolution.x / iResolution.y;
    uv.x *= AR;

    float c = AA(uv, fontWeight);

    //c = 1. - c;
    fragColor = vec4(c, c, c, 1);
