#define PI 3.14159265359

vec3 colorA = vec3(0.912,0.311,0.073);
vec3 colorB = vec3(0.537,1.000,0.207);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = vec3(0.0);

    vec3 pct = vec3(uv.x);

    pct.r = smoothstep(0.46,1., tan(uv.x*iTime*0.1)*5.);
    pct.g = tan(uv.y*PI*2.);
    pct.g *= pow(uv.x,5.0)*sin(iTime*2.)-0.001;
    pct.b = pow(uv.y,0.5);

    col = mix(colorA, colorB, pct);
    col += mix(colorB*col, colorA, pct*pct);

    fragColor = vec4(col,1.0);
}
