#define time iTime

vec3 youtubeHangingStep(vec2 uv, float time){
    vec3 res = vec3(0);
    for(float i = 0.0; i < 3.141592*2.; i+=(3.141592*2.)/10.){
        float r = 0.3;
        vec2 pos = vec2(cos(-time+i), sin(-time+i))*r;
        res += vec3(cos(-time+i)*0.5+0.5)*step(length(uv+pos)*10.0, .5);
    }
    return res;
}

vec3 youtubeHangingSmoothstep(vec2 uv, float time, vec3 ringColor, float radius){
    vec3 res = vec3(0);
    for(float i = 0.0; i < 3.141592*2.-0.1; i+=(3.141592*2.)/10.){
        vec2 pos = vec2(cos(-time+i), sin(-time+i))*radius;
        res += ringColor*smoothstep(0.1, 0.0,length(uv+pos)*1.0);
    }
    return res;
}

vec3 c(vec3 yphase, vec3 amplitude, vec3 frequency, vec3 xphase, float angle){
    return yphase + amplitude * cos((2.*3.141592)*(frequency * angle + xphase));
}

vec3 colors[4] = vec3[4](vec3(0.01, 0.2, 0.4), vec3(0.4, 0.2, 0.01), vec3(0.2, 0.4, 0.01), vec3(0.2, 0.01, 0.2));

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0);
    vec2 uv = fragCoord/iResolution.xy*2.-1.;
    uv.x *= iResolution.x/iResolution.y;
    uv *= 0.35;
    //float squareDist = length(max(uv.x-cos(time), uv.y-sin(time)))*10.0;
    float dir = 1.;
    for(int i = 0; i < 4; i+=1){
        fragColor.rgb += youtubeHangingSmoothstep(uv, dir*time, colors[int(mod(float(i),4.))], float(i+1)*0.07);
        dir *= -1.;
    }
}
