// Green screen function from @iq 

// :D :D :D
vec4 compositeGreenScreenTexture(vec2 uv, vec4 background)
{

     vec4 tex = texture(iChannel0, uv);

     float maxrb = max( tex.r, tex.b );
     float k = clamp( (tex.g-maxrb)*5.0, 0.0, 1.0 );

     float ll = length( tex );
     tex.g = min( tex.g, maxrb*0.8 );
     tex = ll*normalize(tex);

     return mix(tex, background, k);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    float r = 0.0;
    float g = 0.0;
    float b = 0.0;


    float thickness = 1./12.;

    //RED
    float lineNum = floor(((1.5 * uv.x) + uv.y) / thickness);
    bool isRed = mod(lineNum, 3.) == 0.;

    if (isRed) {
        r = 1.0;
    }

    //GREEN
    float speed = 0.1;
    float aniYPos = uv.y + speed * iTime;
    lineNum = floor(((1.5 * uv.x) + aniYPos) / thickness);
    bool isGreen = mod(lineNum, 3.) == 1.;

    if (isGreen) {
        g = 1.0;
    }

    //BLUE
    float aniYNeg = uv.y - speed * iTime;
    lineNum = floor(((1.5 * uv.x) + aniYNeg) / thickness);
    bool isBlue = mod(lineNum, 3.) == 2.;

    if (isBlue) {
        b = 1.0;
    }

    /*
