float d(vec2 p0, vec2 p1) {
    return sqrt(pow(p0.x - p1.x, 2.0) + pow(p0.y - p1.y, 2.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // normalized pixel coordinates
    vec2 uv = fragCoord/iResolution.xy;



    // time varying pixel color
    vec3 col = 0.7 + 0.25*cos(iTime+uv.xyx+vec3(0,2,4)*uv.xyx+vec3(0,2,4));

    vec2 center = vec2(0.5,0.5);
    //move center based on time
    center = center + vec2(sin(iTime*0.5)*0.3,cos(iTime*0.33)*0.1);

    // add distance to center to color
    float distance = d(center, uv);
    col = col - distance;

    // Output to screen
    fragColor = vec4(col,1.0);
}
