mat2 rot(float a){float c = cos(a), s = sin(a);return mat2(c, s, -s, c);}
float calc(float constant, float linear, float exponent, float dist){
    return 1. / (constant + linear *dist + exponent * dist * dist);
}

#define time iTime

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy*2.-1.;
    uv.x *= iResolution.x/iResolution.y;
    uv.y += 0.9;
    uv *= 1.1;
    //uv *= cos(atan(uv.x, uv.y));
    //uv *= rot(time);
    //uv.x = fract(uv.x)-0.5;
    // Output to screen
    float size = 1./0.1;
    float y1 = abs(uv.y - uv.x)*size;
    float y2 = abs(uv.y - sin(uv.x))*size;
    float y3 = abs(uv.y - cos(uv.x*uv.x))*size;
    float attenuation1 = calc(0.5,0.01,2.,uv.x);
    float attenuation2 = calc(1.,0.01,2.,uv.x);
    float attenuation3 = calc(2.5,0.01,2.,uv.x);
    float attenuation4 = calc(3.5,0.01,2.,uv.x);



    vec2 gr = uv*8.;
    vec2 res = fract(gr)-0.5;
    vec3 rescol = vec3(0);
    if(res.x>.46 || res.y>.46){
        rescol = vec3(0.4,0,0);
    }
    if(abs(uv.x)<0.005 || abs(uv.y)<0.005){
        rescol = vec3(1, 1, 0);
    }
    vec4 color = vec4(0);
    color = mix(color, vec4(rescol,1.0), 1.);
    color = mix(color, vec4(0,0.5,0,1), (abs((uv.y) - attenuation1)*10.) < 0.1 ? 1. : 0. );
    color = mix(color, vec4(0.3,0.9,0,1), (abs((uv.y) - attenuation2)*10.) < 0.1 ? 1. : 0. );
    color = mix(color, vec4(0,0.5,0.2,1), (abs((uv.y) - attenuation3)*10.) < 0.1 ? 1. : 0. );
    color = mix(color, vec4(0,0,0.6,1), (abs((uv.y) - attenuation4)*10.) < 0.1 ? 1. : 0. );
    fragColor = vec4(color);
}
