// Hash function
float hash(vec2 p) 
{

    vec2 tr = fract(p * vec2(12.9898, 78.233));

    float LargePrime = 522627.26253;
    float sum = tr.x+tr.y;

    return fract( sin(sum)*LargePrime );

}

// Perlin noise function
float SimpleNoise(vec2 p,float scale) 
{
    p*=scale;

    vec2 i = floor(p);
    vec2 f = fract(p);

    // Calculate the gradients at the corners of the cell
    float topLeft = hash(i);
    float topRight = hash(i + vec2(1.0, 0.0));
    float bottomLeft = hash(i + vec2(0.0, 1.0));
    float bottomRight = hash(i + vec2(1.0, 1.0));

    // Interpolate along x-axis
    float v1 = mix(topLeft, topRight, f.x);
    float v2 = mix(bottomLeft, bottomRight, f.x);

    // Interpolate along y-axis
    return mix(v1, v2, f.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

    float f = SimpleNoise(uv,100.0);
    vec3 col = vec3(f, f, f);

    // Output to screen
    fragColor = vec4(col, 1.0);
}
