float f_n_random_norm(float n){
    return fract(sin(n*11235.1234)*1234.1324);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 a_n_pix_cor_nor = fragCoord.xy / iResolution.xy;

    vec2 a_n_mou_cor_nor = iMouse.xy / iResolution.xy;
    if(iMouse.xy==vec2(0)){
        a_n_mou_cor_nor = vec2(0.5,0.5);
    }
    float n_points = 500.0 * a_n_mou_cor_nor.x;
    float n_dist_min = 1.0; 
    float n_max_dist_hardcoded = 0.2;
    fragColor = vec4(1.0);
    float n_freq_max_factor = 2.0;
    float n_rand_last = f_n_random_norm(1234.1234);
    for(float n_i_norm = 0.0; n_i_norm < 1.0; n_i_norm+=1.0/n_points){
        float n_rand1 = f_n_random_norm(n_rand_last);
        float n_rand2 = f_n_random_norm(n_rand1);
        float n_rand3 = f_n_random_norm(n_rand2);
        float n_rand4 = f_n_random_norm(n_rand3);
        n_rand_last = n_rand1;
        float n_dist = length(
            vec2(
                n_rand1+sin(iTime*n_freq_max_factor*n_rand3+n_i_norm*n_rand3)*n_rand3*0.2, 
                n_rand2+cos(iTime*n_freq_max_factor*n_rand3+n_i_norm*n_rand4)*n_rand4*0.2
            )
            -
            a_n_pix_cor_nor
        );
        if(n_dist < n_dist_min){
            n_dist_min = n_dist;
        }
    }

    fragColor = vec4(n_dist_min/n_max_dist_hardcoded);



}
