float h(in vec2 a) { return dot(a, a); }
float i(in vec2 c, in vec2 a, in vec2 d) {
  vec2 e = c - a, b = d - a;
  return h(e - b * clamp(dot(e, b) / dot(b, b), 0., 1.));
}
mat2 f(float a) {
  float b = sin(a), c = cos(a);
  return mat2(c, b, -b, c);
}
float j(vec2 d) {
  float c = .1;
  for (float a = 0.; a < 24.; a++) {
    float b = a + 1.;
    if (a > 23.)
      break;
    float e = cos(a / 24. * 6. * 6.28) * .1 * (a / 5.),
          k = sin(a / 24. * 6. * 6.28) * .1 * (a / 5.),
          l = cos(b / 24. * 6. * 6.28) * .1 * (b / 5.),
          m = sin(b / 24. * 6. * 6.28) * .1 * (b / 5.);
    c = min(c, i(d, vec2(e, k), vec2(l, m)));
  }
  return c;
}
vec2 g(inout vec2 b, vec2 a) {
  vec2 c = a * .5, d = floor((b + c) / a);
  b = mod(b + c, a) - c;
  return d;
}
float n(vec2 a) {
  float b = .1;
  vec2 c = g(a, vec2(.49, .48));
  a.xy *= mod(c.x, 2.) == 0. ? f(0.) : f(3.141593), b = min(b, j(a * 2.));
  return b;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 a = fragCoord.xy / iResolution.xy;
  a = (a - .5) * 2., a.x *= iResolution.x / iResolution.y;
  vec3 c = vec3(0);
  float b = .1;
  a *= f(.785398), b = n(a), b = smoothstep(0., b, 1e-4), c += b,
                   fragColor = vec4(c, 1);
}
