float rand2(vec2 p)
{

    return fract(sin(dot(p.xy, vec2(13.012842, 23.23))) * 12377.0);

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    vec2 nfragCoord = fragCoord / iResolution.xy * 2.0 - 1.0;
    nfragCoord.x *= (iResolution.x / iResolution.y);

    vec2 sfragCoord = nfragCoord * 3.0;

    vec2 ifragCoord = floor(sfragCoord);
    vec3 color = vec3(step(0.5, rand2(ifragCoord + sin(iTime / 223332.0))));

    float pixel = rand2(nfragCoord);

    fragColor = vec4(color, 1.0);



}
