#define PI 3.141529

float sdStar(vec2 p, float r, in int n, in float m, in float roundness) {
    float an = 3.141593 / float(n);
    float en = 3.141593 / m;
    vec2  acs = vec2(cos(an), sin(an));
    vec2  ecs = vec2(cos(en), sin(en));

    float bn = mod(atan(p.x, p.y), 2.0 * an) - an;
    p = length(p) * vec2(cos(bn), abs(sin(bn)));
    p -= r * acs;
    p += ecs * clamp(-dot(p, ecs), 0.0, r * acs.y / ecs.y);
    return length(p) * sign(p.x) - roundness;
}

float sdLine(vec2 p, vec2 a, vec2 b, float r) {
	p -= a;
	b -= a;
	float d = length(p - b * clamp (dot (p, b) / dot (b, b), 0.0, 1.0));
	return smoothstep(r + 5.0 / iResolution.y, r, d);
}


vec2 hourVec() {
// Vector in the direction of hour with a length of 1
    float hourRads = PI*(-2.0*iDate.w/(3600.0*12.0)+0.5);
    return vec2(cos(hourRads),sin(hourRads));
}

vec2 minVec() {
// Vector in the direction of min with a length of 1
    float minRads = PI*(-2.0*fract(iDate.w/(3600.0))+0.5);

    return vec2(cos(minRads),sin(minRads));
}


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec2 p = (2.0 * fragCoord - iResolution.xy) / iResolution.y;

    float star = sdStar(p, 0.5, 8, 3.0, 0.26);
    float line1 = sdLine(p, 0.283*hourVec()+vec2(-0.1, -0.25), vec2(-0.1, -0.25), 0.045);
    float line2 = sdLine(p, 0.64*minVec()+vec2(-0.1, -0.25), vec2(-0.1, -0.25), 0.045);

    float d = smoothstep(0.0, 5.0 / iResolution.y, star);
    d = max(d, line1);
    d = max(d, line2);

    vec3 blue = vec3(0.11, 0.61, 0.93);

	//vec3 color = mix(vec3(1.0), blue, 1.0 - smoothstep(0.0, 5.0 / iResolution.y, d));
    vec3 color = mix(vec3(1.0), blue, 1.0 - d);

	fragColor = vec4(color, 1.0);
}
