void mainImage( out vec4 O,  vec2 U ) {
    bool bottom = U.y < iResolution.y/2.;

 // U /= .1*iResolution.y;                 // issue with resolution x * 281
    U /= 140.5;                            // some U values give plain integers
    float x = U.x; 
    O = vec4( floor(x) + floor(-x) +1. );  // ... there, floor(-x) != -floor(x)
  //O = vec4( fract(x)+ fract(-x) );       // so there, fract(-x) != 1-fract(x)

    if (bottom) O = vec4(fract(x), fract(-x) ,0,0); 
 // O = vec4( fract(x), bottom ? 1.-fract(x) : fract(-x) ,0,0);
}
