// original https://www.shadertoy.com/view/fsGXWG
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = (fragCoord.xy/iResolution.xy)-.5;
     vec2 uv2 = (fragCoord.yx/iResolution.yx)-.5;   
 uv+= (fragCoord.xy - iResolution.xy * 0.5)/iResolution.y;
  uv2-= (fragCoord.xy - iResolution.xy * 0.5)/iResolution.y;
	float len = length(uv.xy);
	vec2 trs = vec2(sin(iTime*.03),cos(iTime*.003));
    uv*=trs;
    float t = 1.21*iTime;
	float time = t  +  (5.-sin(t))*0.151 / (len+0.77); 
	float si = sin(time), co = cos(time);
	uv *= mat2(co, si, si, -co);                    
uv2*= mat2(co, -si, si, -co);

	float c=1.,c2=1., v1=0., v2=1., v3, v4;  vec3 p,p3;

	for (int i = 0; i < 100; i++) {
		p = .035*float(i) *  vec3(uv, 0.);
        p3 = .035*float(i) *  vec3(uv2, 0.);
		p += vec3(1.22,  .3,  -1.5 -atan(t*1.3)*.1);
           p3 += vec3(0.22,  1.3,  1.5);

		for (int i = 0; i < 20; i++)                
			p = abs(p) / dot(p,p) - 0.359;
            p3 = abs(p) / dot(p3,p3) - 0.359;

		float p2 = dot(p,p)*.0315;
        	float p5 = fract(dot(p3,p3)*.10115);
		v1 += p2 * ( 1.8 + cos(len*5.0  +.5 -t*1.) );
		v2 += p2 * ( 0.5 + sin(len*3.5 +2.2 -t*1.) );
        v2 += p2 * ( 1.5 + sin(len*3.5 +2.2 -t*1.) );
         v3 += p5 * ( 1.5 + cos(len*13.5 +2.2 -t*1.) );

	}

	c = length(p.xy) * 0.175;
    	c2 = length(p3.xy) * 1.175;

	v1 *= smoothstep(.31 , .01, len);
	v2 *= smoothstep(1.6 , .1, len);
	v3  = smoothstep(0.01, .0, len);

	vec3 col = vec3(c,  (v1+c)*.25,  v2*v4);
	col = col  +  v3*.9;                      
	fragColor=vec4(col, 1.);
}
