#define PI 3.1415

float dist(vec2 A, vec2 B){
    return sqrt(pow(A.x - B.x, 2.0) + pow(A.y - B.y, 2.0));
}

mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;

    vec4 color = vec4(.3, clamp(sin(iTime),0.3,0.7),clamp(cos(iTime),0.5,1.),1.0);

    vec4 draw;
    float current_dist;
    vec2 light_source_pos;
    vec2 r_uv;
    for (float i = 0.; i < 8.; i += 1.){

        if(mod(i, 4.) == 0.){
            r_uv = uv * rotate2d(0.+i*0.1);
            current_dist = dist(vec2(r_uv.x/sin(iTime), r_uv.y), vec2(0., 0.));
        }
        if(mod(i, 4.) == 1.){
            r_uv = uv * rotate2d(5.*PI/6.+i*0.1);
            current_dist = dist(vec2(r_uv.x, r_uv.y/sin(iTime)), vec2(0., 0.));
        }
        if(mod(i, 4.) == 2.){
            r_uv = uv * rotate2d(PI/4.+i*0.1);
            current_dist = dist(vec2(r_uv.x/cos(iTime), r_uv.y), vec2(0., 0.));
        }
        if(mod(i, 4.) == 3.){
            r_uv = uv * rotate2d(2.*PI/3.+i*0.1);
            current_dist = dist(vec2(r_uv.x, r_uv.y/cos(iTime)), vec2(0., 0.));
        }


        draw += color * 1./abs(current_dist - .3*i)*0.1;

        draw *= tanh(1.-abs(log(abs(tan(iTime))))/10.);
    }


    fragColor =  draw;
}
