void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2. - iResolution.xy) / iResolution.y;

    float d = length(uv);
    d -= smoothstep(.4, (sin(iTime) + 3.) / 4., abs(uv.y)) - ((sin(iTime) / 4.) - .6);
    d = sin(d * -(abs(uv.y * 3.) + 3.)) / 4.;
    //d = abs(d);
    d = 0.05 / d;




    vec3 col = vec3(d);

    // Output to screen
    fragColor = vec4(col,1.0);
}
