mat3 mats[9];

float cutit(float inp){
return clamp(inp, 0., 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
//The configuration tensor (change these values for different patterns):
mats[0] = mat3(0, .065, 0, 0, 0, 0, .01, -.4, -.5);
mats[1] = mat3(0, .065, 0, 0, 0, 0, .01, -.4, .4);
mats[2] = mat3(0, .065, 0, 0, 0, 0, .01, -.4, -.5);
mats[3] = mat3(0, .065, 0, 0, 0, 0, .01, -.4, .4);
mats[4] = mat3(.98, .065, 0, 0, 0, 0, 0, -.4, .34);
mats[5] = mat3(0, .065, 0, 0, 0, 0, .01, -.4, .4);
mats[6] = mat3(0, .065, 0, 0, 0, 0, .01, -.4, -.5);
mats[7] = mat3(0, .065, 0, 0, 0, 0, .01, -.4, .4);
mats[8] = mat3(0, .065, 0, 0, 0, 0, .01, -.4, -.5);


   vec2 uv = fragCoord.xy / iResolution.xy;

    vec3 rtot = vec3(0.,0.,0.);

    int ind = 0;


//the convolution kernel is just the Moore neighborhood of a given cell
//take the RGB values as vectors and apply a matrix transform specified via the configuration tensor, then sum
    for(float i = -1.0; i <= 1.0; i += 1.0)
    {
        for( float j = -1.0; j <= 1.0; j += 1.0)
        {
        	vec2 offset = vec2(i, j) / iResolution.xy;		 
        	rtot += mats[ind]*texture(iChannel0, uv + offset).rgb;
            ind++;
        }
    }    

    vec3 rfin = vec3(cutit(rtot.x), cutit(rtot.y), cutit(abs(sin((rtot.z)))));       

//initialize cells   
    if(iFrame < 3) {
    if(abs(uv.x-.5)<.001 && abs(uv.y-.5)<.002){
    rfin = vec3(1.,1.,1.);
    }else{
    rfin = vec3(0.,0.,0.);}
    }

//paint on pixels manually with the mouse
    if(iMouse.z > 0.0 && abs(fragCoord.x-iMouse.x)<1.0 && abs(fragCoord.y-iMouse.y)<1.0){
    rfin = vec3(1.0,1.0,1.0);
    }



    fragColor = vec4(rfin,1.0);
}

//anti-flicker buffer
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;    
    vec4 col1 = texture(iChannel0, uv);
    vec4 col2 = texture(iChannel1, uv);
    fragColor = (col1+col2)*.55;
}
