/**
 * Draw a rectangle pos with width/height/color
 * The position is topleft in pixels
 */
vec4 rectangle(vec2 uv, vec2 pos, float width, float height, vec3 color) {
	float t = 0.0;
	if (uv.x >= pos.x && (uv.x < pos.x + width)
		&& (iResolution.y - uv.y) >= pos.y && ((iResolution.y - uv.y) < pos.y + height)) {
		t = 1.0;
	}
	return vec4(color, t);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy;
	float width = iResolution.x / 12.;
	float height = width;

	// colors
    vec4 background = vec4(0.384,0.216,0.306,1.0);
    vec3 color = vec3(0.992,0.773,0.482);

    // rect
	vec4 rect1 = rectangle(uv, vec2(width, width), width, height, color);
    vec4 rect2 = rectangle(uv, vec2(width, iResolution.y - 2. * height), width, height, color);
    vec4 rect3 = rectangle(uv, vec2(iResolution.x - 2. * width, width), width, height, color);
    vec4 rect4 = rectangle(uv, vec2(iResolution.x - 2. * width, iResolution.y - 2. * height), width, height, color);

	// mix background & rect
	fragColor = mix(background, rect1, rect1.a);
    fragColor = mix(fragColor, rect2, rect2.a);
    fragColor = mix(fragColor, rect3, rect3.a);
    fragColor = mix(fragColor, rect4, rect4.a);
}
