float n_pts = 2.;
float points[6] = float[6](-0.3, 0.3, 4., 0., 0., 6.);

float k = 70.;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float max_time = 1.;

    vec2 uv = fragCoord/iResolution.xy;
    uv -= vec2(0.5);
    uv.x *= iResolution.x/iResolution.y;

    vec3 col = vec3(0.);

    for(int i = 0; i < 3*int(n_pts); i+=3)
    {
        vec2 pos = vec2(uv.x-points[i], uv.y - points[i+1]);

        float r = sqrt(pos.x*pos.x + pos.y*pos.y);

        float m = iTime*0.07;
        float sharpness = 700.;
        float exponent = r-m;
        float life_time = (points[i+2] - iTime)/points[i+2];
        if(life_time < 0.){
            life_time = 0.;
        }

        float a = life_time*exp(-sharpness*(exponent*exponent));
        float wave = sin(k*r-k*m);
        wave *= wave*wave;

        float y = a*wave;

        col += vec3(y);
    }    

    fragColor = vec4(col,1.0);
}
