vec2 hash( vec2 p)
{
    return fract(sin((p)*mat2(127.1,311.7,269.5,183.3))*43758.5453) ;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y +iTime/5.;

    uv*=10.;

    //grid size
    vec2 r = vec2(1,1.73);
    vec2 h = r*.5;

    //offset uv grids
    vec2 a = mod(uv,r)-h;
    vec2 b = mod(uv-h,r)-h;

    //hexagonal space
    vec2 gv;
    if (length(a)<length(b))
    {
        gv = a;
    }
    else
    {
        gv = b;
    }

    vec2 id = uv-gv+.5;

    //color
    vec3 col = vec3(hash(id),0.);
    fragColor = vec4(col,1.0);
}
