void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    float anchor = (fragCoord.xy / iResolution.xy).y;

    vec3 blue = vec3(0.0, 0.7, 0.85);
    vec3 pink = vec3(0.85, 0.3, 0.5);
    vec3 bgcolor = vec3(1.0, 1.0, 1.0);


    // divide the y coordinate for the screen into 5 & get current anchor position
    int flagPosition = int(anchor / 0.2);
    if (flagPosition == 0 || flagPosition == 4) {
        bgcolor = blue;
    } else if (flagPosition == 1 || flagPosition == 3) {
        bgcolor = pink;
    }

    fragColor = vec4(bgcolor, 1.0);
}
