// increase this if you have a very fast GPU
#define AA 2


vec2 complexPow (in vec2 z, in float x )
{
    float r = sqrt(z.x*z.x+z.y*z.y);
    float phi = atan(z.y, z.x);
    float phi2 = x*(phi + 3.1415926);
    vec2 newZ = pow(r, x) * vec2(cos(phi2), sin(phi2));
    return newZ;
}

vec2 absComponents(in vec2 z) {return vec2(abs(z.x), abs(z.y));}
float cabs(in vec2 z) {return sqrt(z.x*z.x+z.y*z.y);}

float mandelbrot( in vec2 c )
{
    /*#if 1
    {
        float c2 = dot(c, c);
        // skip computation inside M1 - https://iquilezles.org/articles/mset1bulb
        if( 256.0*c2*c2 - 96.0*c2 + 32.0*c.x - 3.0 < 0.0 ) return 0.0;
        // skip computation inside M2 - https://iquilezles.org/articles/mset2bulb
        if( 16.0*(c2+2.0*c.x+1.0) - 1.0 < 0.0 ) return 0.0;
    }
    #endif*/


    const float B = 1e10;
    //float l = 0.0;
    float es = 0.0;
    float es2 = 0.0;
    vec2 z  = vec2(0.0);
    for( int i=0; i<25; i++ )
    {
        vec2 zold = z;
        z = complexPow(absComponents(complexPow(z, 5.)), 0.25) + c;
        vec2 dz = z - zold;
        es += exp(-cabs(z));
        es2 += exp(-1./cabs(zold -z));
        if( dot(z,z)>(B*B) ) return es;
        //l += 1.0;
    }

    //if( l>511.0 ) return 0.0;

    // ------------------------------------------------------
    // smooth interation count
    //float sl = l - log(log(length(z))/log(B))/log(2.0);

    // equivalent optimized smooth interation count
    //float sl = l - log2(log2(dot(z,z))) + 4.0;

    //float al = smoothstep( -0.1, 0.0, sin(0.5*6.2831*iTime ) );
    //l = mix( l, sl, al );

    return es2;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(0.0);

#if AA>1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        vec2 p = (-iResolution.xy + 2.0*(fragCoord.xy+vec2(float(m),float(n))/float(AA)))/iResolution.y;
        float w = float(AA*m+n);
        float time = iTime + 0.5*(1.0/24.0)*w/float(AA*AA);
#else    
        vec2 p = (-iResolution.xy + 2.0*fragCoord.xy)/iResolution.y;
        float time = iTime;
#endif

        float zoo = 0.92 + 0.18*cos(.07*time);
        float coa = cos( 0.05*(1.0-zoo)*time );
        float sia = sin( 0.05*(1.0-zoo)*time );
        zoo = pow( zoo,8.0);
        vec2 xy = vec2( p.x*coa-p.y*sia, p.x*sia+p.y*coa);
        vec2 c = vec2(-5.,-5.3186) + xy*zoo;

        float l = mandelbrot(c);
        l = 7.*l;

        col += 0.5 + 0.5*cos( 3.0 + l*0.15 + vec3(0.0,0.6,1.0));
#if AA>1
    }
    col /= float(AA*AA);
#endif

    fragColor = vec4( col, 1.0 );
}
