void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec2 uv = (fragCoord)/iResolution.y*4.;
    uv.y += iTime/2.;
    vec3 col = vec3(0);
    vec2 offset = vec2(0.),
    uv1 = uv;
    uv.x += sin(iTime * uv.y * 0.02)  * 0.05;
    uv.y += cos(iTime * uv.x * 0.02)  * 0.02;

    for(int k = 0; k < 5; k++){
        uv =
            abs(fract(uv.yx+floor(uv1.y+floor(uv.x))/pow(2.,mod(floor(uv.x-iTime/2.),3.)))-(1.5*iTime*0.01))
            //abs(fract(uv.yx+floor(uv1.y+floor(uv.y*8.)/8.)/8.)-.5)
            //abs(fract(uv.yx+sign(uv.y-uv.x)*floor(uv1.y+floor(uv.y))/8.)-.5)
        ;
        if(uv.y < uv.x) col =
            //vec3(col.yz,abs(min(uv.x,uv.y)))
            vec3(col.yz,abs(max(uv.x,uv.y)))
        ;
    }
    fragColor = vec4(min(col*2.,vec3(1.)),1.0);
}
