float line(vec2 p, vec2 q, vec2 st, bool inv) {
    float a = (q.y - p.y)/(q.x-p.x);
    float b = p.y - a * p.x;

    float s = smoothstep(b, b+0.02, - st.x*a + st.y);
    if (inv) {
        s = 1.0 -s;
    }
	return s;
}

float heart(vec2 p, float r, vec2 st) {
    float d1 = distance(st, p);
    float d2 = distance(st, vec2(-0.25, 0.25));
	// c = smoothstep(c, c+0.1, 0.5);

    float c = smoothstep(d1, d1+0.01, r);

    vec2 q = p + vec2(-r*1.414/2.0) + vec2(1.0, -1.0);
    float s = line(p + vec2(-r*1.414/2.0), q, st,false );

    float e = step(d2, 0.2);

    // c += smoothstep(c-0.1, c, 0.2);

    return c + s * step(st.y, p.y - r*1.414/2.0);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 st = fragCoord/iResolution.xy;
    st -= 0.5;
    st.x *= iResolution.x/iResolution.y;
    st *= 2.0;
    if (st.x > 0.0) {
        st.x = -st.x;
    }

    vec2 p1 = vec2(-0.130 - sin(iTime)/40., sin(iTime*2.)/2.0);

    vec2 p2 = vec2(-0.130 - sin(iTime+0.1)/40., sin(iTime*2. + 0.1)/2.0);
    vec2 p3 = vec2(-0.130 - sin(iTime+0.2)/40., sin(iTime*2. + 0.2)/2.0);

    float r1 = 0.2 + sin(iTime)/50.0;
    float r2 = 0.2 + sin(iTime + 0.1)/50.0;
    float r3 = 0.2 + sin(iTime + 0.2)/50.0;


    float c1 = heart(p1, r1, st);
    float c2 = heart(p2, r2, st);
    float c3 = heart(p3, r3, st);

    vec3 col = vec3(c3, c2,c1);

    fragColor = vec4(col,1.0);
}
