void mainImage( out vec4 o, in vec2 i )
{
    o= vec4(0.);
	for(float j=0.; j<=99.; j+=1.){
    for(float k=0.; k<=99.; k+=1.){
    if(distance(i, vec2((j*7.+k*3.5-130.)*(iResolution.x/800.),(k*6.062-80.)*iResolution.y/450.))<2.){
    o = texelFetch(iChannel0, ivec2(j,k), 0);
    break;
    }
    }}

}

ivec2 wrap(float x, float y){
return ivec2(mod(x,100.),mod(y,100.));
}

void mainImage( out vec4 o, in vec2 i )
{
    o = vec4(0.);
    if(max(i.x,i.y)>99.){
    return;}

    //ruleset parameters for 3 different neighborhoods: c = the cell, b = directly adjacent cells, a = "diagonal" cells
    float a = -1./3.;
    float b = 1./3.;
    float c = 0.1;

    float rtot = 0.;
    for(float j=-1.; j<=1.; j+=1.){
    for(float k=-1.; k<=1.; k+=1.){
        vec4 v = texelFetch(iChannel0, wrap(i.x+j,i.y+k),0);
        if(abs(j)==1. && k==j){
        rtot+= v.x*a;
        continue;}
        if(j==0. && k==0.){
        rtot+= v.x*c;
        continue;}
        rtot+= v.x*b;
    }}
    rtot+= float(texelFetch(iChannel0, wrap(i.x+1.,i.y-2.),0).x*a);
    rtot+= float(texelFetch(iChannel0, wrap(i.x-1.,i.y+2.),0).x*a);
    rtot+= float(texelFetch(iChannel0, wrap(i.x+2.,i.y-1.),0).x*a);
    rtot+= float(texelFetch(iChannel0, wrap(i.x-2.,i.y+1.),0).x*a);
    o = vec4(clamp(abs(rtot),0.,1.));

    if(iFrame<2){
    if(i.x>50. && i.y>50.){
    if(i.x<51. && i.y<51.){
    o = vec4(1.);
    }}}
}
