//Inspired by https://www.shadertoy.com/view/clS3WW
float f_n_rand(float n){
    n += n*iTime*0.001;
    n = sin(n*0.393+n*2.34);
    return fract(n);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float n_ratio_1to_iresx = 1./ iResolution.x;
    vec2 o_trn_fc_nor = fragCoord.xy / iResolution.xy;

    float n_ratio_x_to_y = iResolution.x / iResolution.y; 

    vec2 o_trn_fc_nor_offset = (o_trn_fc_nor - vec2(0.5))*vec2(n_ratio_x_to_y, 1.);

    float n_its = 50.;
    float n_dmin = 1.;
    float n_it_dmin = 0.;
    float n_dprod = 1.;
    float n_dsqrtprod = 2.;
    float n_dist_secondmin = 1.;
    for(float n_it = 0.; n_it < n_its; n_it+=1.)
    {
        float n_t = floor(iTime)*0.1;
        vec2 o_prand = vec2(
            (f_n_rand(n_it+n_t)-0.5)*(2.+(iResolution.x/iResolution.y)), 
            (f_n_rand(n_it*3.123+n_t)-0.5)*2.
        );

        float n_dist = length(o_trn_fc_nor_offset - o_prand);
        n_dprod *= n_dist;
        n_dsqrtprod *= pow(n_dist, 1./10.);

        if(n_dist < n_dmin){
            n_dmin = n_dist; 
            n_it_dmin = n_it;
        }
    }
    float a_n =  n_it_dmin /n_its;
    fragColor = vec4(a_n);

}
