void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = ((fragCoord - .5 * iResolution.xy)/iResolution.y) * 3.;
    vec3 col;
    float PD = length(uv)-.5 - sin(uv.x + 1.5)/2. - .05 * cos(uv.x + iTime);
    col.g += .02/PD;
    if(PD < 0.){
        col.g = 1.;

        float a = length(uv *10.) - iTime;
        col.g -= sin(length(uv) * 10.-iTime);
        col.g += .1/sin(length(uv) * 10.-iTime);
        uv *= mat2(cos(a),-sin(a),sin(a),cos(a));
         uv.y = abs(uv.y) +.1;
         uv.y -= sin(uv.x * 20.) * .1;
        col += (.2/uv.y) - sin(uv.x + 1.5)/2. - .05 * cos(uv.x + iTime);;
    }

    fragColor = vec4(col,1.0);
}
