void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 c = (fragCoord.xy / iResolution.xy - 0.5) * 4.0;
    c.x -= 0.5;
    c.y += 0.25;

    float zoom = pow(1.1, iTime);
    c /= zoom;

    vec2 z = vec2(0.0);

    float n = 0.0;

    for (int i = 0; i < 300; i++)
    {
        z = vec2(z.x * z.x - z.y * z.y, 2.0 * z.x * z.y) + c;

        if (length(z) > 2.0)
        {
            n = float(i) / 300.0;
            break;
        }
    }

    fragColor = vec4(vec3(n), 1.0);
}
