void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy - iResolution.xy * 0.5) / 110.0;

	float t = iTime * 0.2;
	float sq = sqrt(uv.x * uv.x + uv.y * uv.y);
	float a = atan(uv.x, uv.y);
	float c = cos(sq * 4.0 - t * 2.0);
	float s = sin(sq * 3.0 + t * 3.0);

	float col = 0.0;
	col += sin(s + sq * c * 2.0 + t);
    col += sin(s + sq * c * 2.0 * sin(t * 1.5) + a * 6.0);
    col /= 2.0;

	fragColor = vec4(1.5*col*c,-1.0*col*c,-1.5*col*c,1);
}
