precision mediump float;

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec4 u_color0 = vec4(0.125,0.643,0.843,1.0);
    vec4 u_color1 = vec4(0.247,0.545,0.918,1.0);
    vec4 u_color2 = vec4(0.506,0.282,0.925,1.0);
    vec4 u_color3 = vec4(0.706,0.337,0.847,1.0);
    vec4 u_color4 = vec4(0.271,0.463,0.914,1.0);
    float currentAngle = (iTime * 36.0); //Add minus to rotate counter-clockwise
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 origin = vec2(0.5, 0.5);
    uv -= origin;
    float angle = radians(90.0) - radians(currentAngle) + atan(uv.x, uv.y);
    float len = length(uv);
    uv = vec2(cos(angle) * len, sin(angle) * len) + origin;
    vec4 tltr = mix(u_color0, u_color1, smoothstep(0.0,1.0,uv.x));
    vec4 blbr = mix(u_color2, u_color3, smoothstep(0.0,1.0,uv.x));
    vec4 color = mix(tltr, blbr, smoothstep(0.0,1.0,uv.y));
    fragColor = color;
}
