// from
// https://twigl.app/?ol=true&ss=-NNIajM4aEzy75lqtAUd
mat2 rotate2D(float a)
{
    return mat2(cos(a), sin(a), -sin(a), cos(a));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    // new uvs
    vec2 n,q,p = (fragCoord.xy-.5*iResolution.xy) / iResolution.y;
    float d = dot(p,p),
        S=4.,
        i,a,j;

    for(mat2 m=rotate2D(5.);j++<30.;)
    {
        p *= m;
        n *= m;
        q=p*S+iTime*4.+sin(iTime*4.-d*6.)*.8+j+n;
        a+=dot(cos(q)/S,vec2(.2));
        n-=sin(q);
        S*=1.2;
    }

    float cb = abs(sin(iTime)) * 4.0; 

    vec4 col = (a+.2)*vec4(.5,0.,cb,0)+a+a-d;
    fragColor = vec4(col);
}
