vec2 triangle_wave(vec2 a){
    vec2 a2 =
        vec2(1.,0.5)
    ,
    a1 = a+a2;
    return
        abs(fract((a1)*(a2.x+a2.y))-.5)
    ;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col = vec3(0.);
    float t1 = 1.;
    vec2 uv = (fragCoord)/iResolution.y/t1/2.0;
    uv += vec2(iTime/2.0,iTime/3.0)/t1/8.0;
    if(iMouse.z>.5)
    uv = uv.xy + iMouse.xy / iResolution.xy/t1;
    vec2 t2 = vec2(0.);
    bool b = true;
    for(int k = 0; k < 6; k++){
        vec2 uv1 = uv;
        uv =
            abs(.5+uv+t2)/1.5
        ;
        b = uv.y<uv.x;
        if(b) uv = uv.yx;
        t2 =
            -triangle_wave(uv+.5)
        ;
        uv =
            t2-triangle_wave(uv.yx)
        ;
        {t2 = t2.yx; uv=uv.yx;}
        col.x =
            abs(float(b)-col.x*max(sign(t2.x-t2.y),0.))
            //fract(col.x+iTime+length(uv));
        ;
        col.xyz = abs(col.yzx-float(b)/2.);
    }
    if (!b) col = 1.-col;
    fragColor = vec4(min(col,vec3(1.)),1.0);
}
