// https://github.com/cristian-5

#define time iTime
#define screen iResolution

void mainImage(out vec4 color, in vec2 coord) {
    vec3 c;
	float l, z = time;
    vec2 uv = coord.xy / screen.xy;
    vec2 p = uv - 0.5;
    p.x *= screen.x / screen.y;
	for (int i = 0; i < 3; i++) {
		vec2 q = uv;
		z += 0.7;
		l = length(p);
		q += p / l * (cos(z) / z + 1.0) * (abs(sin(l * 3.0 - z)) + 0.5);
		c[i] = 0.2 / length(mod(q, 1.0));
	}
    color = vec4(c / l, 1.0);
}
