void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / max(iResolution.y, iResolution.x);
    vec2 uv0 = (fragCoord.xy *2.0 - iResolution.xy) / max(iResolution.y, iResolution.x);

    vec2 mymouse = iMouse.xy / iResolution.xy;
    float d = length(uv0);
    float vol = texture(iChannel0, vec2(0.5)).x / 10.0;
    d = sin((iTime * 1.25 * vol) - d * 15.0) / 15.0;
    d = abs(d);

    vec3 color = vec3(fract(mymouse.x * uv.x), fract(mymouse.y * uv.y), smoothstep(fract(iTime), 0.25, 0.50)) * (sin(vol) + 1.0 / 2.0);
    color += d * 2.5;
    vec3 color1 = vec3(d, d, d);

    fragColor = vec4(color, 1.0);
}

