mat2 rotate2D(float rad){
    return mat2(cos(rad), sin(rad), -sin(rad), cos(rad));
}

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.2,e; vec3 p,q=vec3(-1,0,-1);
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*iResolution.xy)/iResolution.y,e)*.4;
    p.xz*=rotate2D(iTime*.2);

    for(j=0.;j++<2.5;)
        p=abs(abs(reflect(p,q))-.04)-.015,
        d=max(min(p.x,p.z),p.y),
        d-=dot(p,q)/2e4,
        e+=d;

}o+=5./i;
}
