#define dt sqrt(iTime*80000.)
Main {
    int I = iFrame % ( 1 + 2 + 1 + 2 );
    U = floor(U);
    vec2 u = 2.*(U-.5*R)/R.y;
    if (I == 0) {
        // Phase Space
        Q = A(U);
        //if (iFrame < 1) {
            float l = length(U-.5*R);
            Q.y = 0.;
            Q.x = smoothstep(50.,25.,l);

       //}
    } else if (I == 1) {
        // x forward pass
        Q = vec4(0);
        vec2 w = 2.*pi*vec2(U)/R;
        for (float i = 0.; i < R.x; i++) {
            vec4 a = A(vec2(i,U.y));
            Q.xy += a.xy*ei(w.x*i);
            //Q.zw += a.zw*ei(w.x*i);
        }
    } else if (I == 2) {
        // y forward pass
        Q = vec4(0);
        vec2 w = 2.*pi*vec2(U)/R;
        for (float i = 0.; i < R.y; i++) {
            vec4 a = A(vec2(U.x,i));
            Q.xy += a.xy*ei(w.y*i);
            //Q.zw += a.zw*ei(w.y*i);
        }
    } else if (I == 3) {
        // Frequency Space
        vec2 w = 2.*pi*(fract(vec2(U)/R+.5)*2.-1.);
        Q = A(U);
        Q.xy = Q.xy*ei(dt*length(w));
    } else if (I == 4) {
        // x reverse pass
        Q = vec4(0);
        vec2 w = 2.*pi*vec2(U)/R;
        for (float i = 0.; i < R.x; i++) 
            Q.xy += A(vec2(i,U.y)).xy*ei(-w.x*i);
        Q /= R.x;
    } else if (I == 5) {
        // y reverse pass
        Q = vec4(0);
        vec2 w = 2.*pi*vec2(U)/R;
        for (float i = 0.; i < R.y; i++) 
            Q.xy += A(vec2(U.x,i)).xy*ei(-w.y*i);
        Q /= R.y;
    }

}

