        vec4 n = A(U+vec2(0,1));
        vec4 e = A(U+vec2(1,0));
        vec4 s = A(U-vec2(0,1));
        vec4 w = A(U-vec2(1,0));
        Q.z = dot(e.xy,e.xy)-dot(w.xy,w.xy);
        Q.w = dot(n.xy,n.xy)-dot(s.xy,s.xy);
        Q.xy = A(U).xy;
        Q.zw *= 100.;
    } 
}

