    for (float x = 0.; x < 6.*S; x++)
    for (float y = 0.; y < S; y++) {
        vec2 u = .75*(vec2(x,y+S*float(I))-3.*S);
        vec4 b = B(U+u);
        vec2 v = b.zw*400.+u;
        vec4 q = .5+10.*vec4(b.x-b.y,b.x,b.y,1);
        v = abs(v);
