// boxIFS
float boxIFS(vec3 p, int iter) {
    for(int i=0; i<iter; i++) {
        p = abs(p) - 1.;
        p.xz *= Rotate(1.);
        p.xy *= Rotate(1.);
    }

    float d = boxSDF(p, vec3(0.5));

    return d;
}

// GetDist
float GetDist(vec3 p) {
    float d=0.;


    // d = roundBoxSDF(bPos, vec3(.5, .5, .5), .1);
    p = mod(p-2., 4.) -2.;
    p.xy = pmod(p.xy, 8.);
    p.xy *= Rotate(sin(iTime));
    p.yz *= Rotate(iTime);
    d = boxSDF(p, vec3(1));
    d = boxIFS(p, 3);

    return d;
}

// RayMarch
Dist RayMarch(vec3 ro,vec3 rd)
{
    float dO = 0.;//Distane Origin
    float ds = 0.;
    float ac = 0.0;
    float ac2 = 0.0;
    for(int i=0; i<99; i++)
    {
        vec3 p = ro + rd * dO;
        ds = GetDist(p);// ds is Distance Scene

        // Phantom Mode
        // 通常のraymarchingとは違って、最短距離の絶対値で進む距離が0にならないようにする
        ds = max(abs(ds), 0.02);
        ac += exp(-ds*3.);
        float a = exp(-ds*3.);

        if( mod(length(p) + 30.*iTime, 30.0) < 3.0 ) {
            ac2 += a;
        }

        dO += ds*0.5;
        // if(dO > MAX_DIST || ds < SURF_DIST)
        // break;
    }
    Dist dist;
    dist.d = dO;
    dist.ac = ac;
    dist.ac2 = ac2;
    dist.ds = ds;
    return dist;
}

// GetNormal
vec3 GetNormal(vec3 p) { 
  float d = GetDist(p); // Distance
  vec2 e = vec2(.01,0); // Epsilon

  vec3 n = d - vec3(
    GetDist( p - e.xyy),  // e.xyy is the same as vec3(.01,0,0).
    GetDist( p - e.yxy),
    GetDist( p - e.yyx));

  return normalize(n);
}

 // GetLight
float GetLight(vec3 p) { 
  // Directional light
  vec3 lightPos = vec3(5.*sin(iTime),5.,5.0*cos(iTime)); // Light Position
  vec3 l = normalize(lightPos - p); // Light Vector
  vec3 n = GetNormal(p); // Normal Vector

  float dif = dot(n, l); // Diffuse light
  dif = clamp(dif, 0., 1.); // Clamp so it doesnt go below 0

  // Shadows
  float d = RayMarch(p+n*SURF_DIST*2., l).d; 

  if(d<length(lightPos-p)) dif *= .1;
  return dif;
}

vec3 ro = vec3(0, 0, -4);
vec3 target = vec3(0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 uv2 = (2. * (fragCoord.xy) - iResolution.xy) / iResolution.x;  //現在の画素位置を0.0-1.0に正規化

    // Camera
    float fov = 65.0;

    ro = vec3(0, 5.*cos(iTime), -4. - 5.*sin(iTime));// Ray Origin/Camera
    target = vec3(0);

    vec3 up = vec3(0, 1, 0);
    vec3 fwd = normalize(target - ro);
    vec3 right = normalize(cross(up, fwd));
    up = normalize(cross(fwd, right));

    vec3 rd = normalize(right * uv2.x + up * uv2.y + fwd / tan(fov * TAU / 720.));

    // for use light
    /* float d = RayMarch(ro,rd).d;// Distance

    vec3 p = ro + rd * d;
    float dif = GetLight(p); // Diffuse lighting

    vec3 color = vec3(dif);

    fragColor = vec4(color, 1.0); */
    // - for use light


    Dist d = RayMarch(ro, rd);// Distance

    /* if (d.ds < 0.001) {
        fragColor = vec4(1);
    } else {
        fragColor = vec4(0);
    } */
    float ac = d.ac;
    float ac2 = d.ac2;
    vec3 col = vec3(ac * 0.01, ac * 0.011 + ac2*0.002, ac * 0.012+ ac2*0.005);
    fragColor = vec4(col, ac * 0.001);
}

/////////////////////////////
// Constants
/////////////////////////////
#define PI 3.1415925359
#define PI2 PI*2.
#define TAU PI*2.
#define MAX_STEPS 100// Mar Raymarching steps
#define MAX_DIST 100.// Max Raymarching distance
#define SURF_DIST .001// Surface Distance


/////////////////////////////
// struct
/////////////////////////////
struct Dist {
    float d;
    float ds;
    float ac;
    float ac2;
};

/////////////////////////////
// primitives
/////////////////////////////
// Round Box - exact
float roundBoxSDF( vec3 p, vec3 b, float r ) {
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0) - r;
}

// Box - exact 
float boxSDF( vec3 p, vec3 b ) {
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float sphereSDF(vec3 p,float r){
    return length(p) - r;
}

/////////////////////////////
// transform
/////////////////////////////

// Rotate
mat2 Rotate(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c,-s,s,c);
}

vec2 pmod(vec2 p, float r) {
    float a =  atan(p.x, p.y) + PI/r;
    float n = PI2 / r;
    a = floor(a/n)*n;
    return p*Rotate(-a);
}

/////////////////////////////
// Smooth blending operators
/////////////////////////////

float smoothIntersectSDF(float distA, float distB, float k ) {
  float h = clamp(0.5 - 0.5*(distA-distB)/k, 0., 1.);
  return mix(distA, distB, h ) + k*h*(1.-h); 
}

float smoothUnionSDF(float distA, float distB, float k ) {
  float h = clamp(0.5 + 0.5*(distA-distB)/k, 0., 1.);
  return mix(distA, distB, h) - k*h*(1.-h); 
}

float smoothDifferenceSDF(float distA, float distB, float k) {
  float h = clamp(0.5 - 0.5*(distA+distB)/k, 0., 1.);
  return mix(distA, -distB, h ) + k*h*(1.-h); 
}

float rand(float n){return fract(sin(n) * 43758.5453123);}

float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

float noise(float p){
	float fl = floor(p);
  float fc = fract(p);
	return mix(rand(fl), rand(fl + 1.0), fc);
}
