/*
 * Create the player object(Piramid) and handle input transformation and rotation
*/
float createPlayer(vec3 point, vec3 originPos, vec3 offset) {

    /// Get Rotation radians and matrix
    float rotationRad = texelFetch(iChannel0, ivec2(P_ROTATION_COL, PLAYER_LAYER_ROW), 0).x;
    mat2 rotationMat = Rotate(rotationRad);

    /// Player body
    Piramid body = Piramid(originPos, PLAYER_HEIGHT);
    float playerHeight = body.height / 2.;

    /// Handle body rotation and body movement
    vec3 bodyPos = point - body.pos;
    bodyPos -= offset;

    // Triangle from the ass
    vec3 trianglePos = point - body.pos;
    trianglePos -= offset;

    mat2 rotate180 = Rotate(PI);
    mat3 rotationMat2 = RotateOffset(rotationRad, 0., -PLAYER_HEIGHT);
    mat3 translationMat = Translate3(0., -PLAYER_HEIGHT);
    float prevZ = trianglePos.z;
    trianglePos.z = 1.;
    trianglePos.xy *= rotate180;
    trianglePos *= translationMat;
    trianglePos *= rotationMat2;
    trianglePos.z = prevZ;


    bodyPos.xy *= rotationMat;

    /// Flatten the piramid
    bodyPos *= vec3(1., 1., 2.);
    trianglePos *= vec3(1., 1., 2.);

    vec2 controls = texelFetch(iChannel0, ivec2(P_CONTROLS_COL, PLAYER_LAYER_ROW), 0).xy;

    if(controls.y != 1.){
        return sdPyramid(bodyPos, playerHeight, body.height / 10., body.height / 1.5) / 2.;
    }
    else{
        return min(sdPyramid(trianglePos, playerHeight/2.5, body.height / 25., body.height / 3.75) / 2., 
                   sdPyramid(bodyPos, playerHeight, body.height / 10., body.height / 1.5) / 2.);
    }
}



float createAsteroids(vec3 point) {
    float distToSphere = 99999.;

    vec2 screenSize = texelFetch(iChannel0, ivec2(C_SCREEN_SIZE_COL, CAMERA_LAYER_ROW), 0).xy;

    int num_asteroids = int(NUM_ASTEROIDS);
    for (int i = 0; i < num_asteroids; ++i) {
        Sphere sph = Sphere(vec3((
            texelFetch(iChannel0, ivec2(i, 0), 0).xy * 2. - 1.
        ) * screenSize, 0.), 1.);
        if (POTATO == 1) {
            distToSphere = min(distToSphere, sdAsteroid(point - sph.pos, sph, i));    
        } else {
            distToSphere = min(distToSphere, sdSphere(point - sph.pos, sph));
        }
    }

    return distToSphere;
}

float createProjectiles(vec3 point) {
    float distToProjectiles = 99999.;

    vec2 screenSize = texelFetch(iChannel0, ivec2(C_SCREEN_SIZE_COL, CAMERA_LAYER_ROW), 0).xy;

    int num_projectiles = int(NUM_PROJECTILES);
    for (int i = 0; i < num_projectiles; ++i) {
        Sphere sph = Sphere(vec3((
            texelFetch(iChannel0, ivec2(i, PROJECTILE_LAYER_ROW), 0).xy * 2. - 1.
        ) * screenSize, 0.), PROJECTILE_RADIUS);
        distToProjectiles = min(distToProjectiles, sdSphere(point - sph.pos, sph));
    }

    return distToProjectiles;
}


/* Get minimal distance to each object, objects are generated here for now */
vec4 getColAndDist(vec3 point) {
    // vec3 bp = vec3(5., 0., 0.);
    // float distToBox = sdBox(point - bp, Box(1., 1., 1., bp));

    vec2 offset = texelFetch(iChannel0, ivec2(0, 1), 0).xy;
    float distPiramid = createPlayer(point, vec3(0.), vec3(offset, 0.));

    float distAsteroids = createAsteroids(point);
    float distProjectiles = createProjectiles(point);

    /// Use respective colors for respective objects
    float hp = 0.3 - texelFetch(iChannel0, ivec2(P_COLLISION_COL, PLAYER_LAYER_ROW), 0).x;
    vec3 player_color = vec3(0.);
    if(hp == 0.3) {player_color = PLAYER_COLOR_3;}
    if(abs(hp - 0.2) < 0.01) {player_color = PLAYER_COLOR_2;}
    if(abs(hp - 0.1) < 0.01) {player_color = PLAYER_COLOR_1;}
    vec3 projectile_color = player_color;

    vec4 colDist = minSd(vec4(player_color, distPiramid), vec4(ASTEROID_COLOR, distAsteroids));
    colDist = minSd(vec4(projectile_color, distProjectiles), colDist);

    /// render event horizon
    colDist = minSd(vec4(vec3(0.), length(point - BH_pos) - BH_R), colDist);

    return colDist;
}

/* Function for getting a normal to the plane */
vec3 getNormal(vec3 point) {
    float dist = getColAndDist(point).w;
    vec2 offset = vec2(.01, 0);

    vec3 normal = dist - vec3(
        getColAndDist(point - offset.xyy).w,
        getColAndDist(point - offset.yxy).w,
        getColAndDist(point - offset.yyx).w
    );

    return normalize(normal);
}

/*
 * Handle lighting and shadows
 * Takes current light level, new light position and a point
 * Takes color
 */
vec3 getLighting(vec3 point, vec3 lightPos, vec3 color) {
    vec3 lightDir = normalize(lightPos - point);
    vec3 normal = getNormal(point);
    float lightIntencity = clamp(dot(normal, lightDir)*.5+.5, 0., 1.);
    // Get shadows
    // Use a point a bit off so the loop does not immediately end
    // vec3 stopColor = vec3(0.);
    // vec3 lightStopPoint = blackHoleRender(point + normal * SURF_DIST * 2., lightDir, stopColor).xyz;
    // if (length(point - lightStopPoint) > SURF_DIST) { lightIntencity *= .3; }
    return color * lightIntencity;
}




/// returns 0 when fetch is at border of black hole reached
float fetchPrecomputed(vec2 fragCoord, inout vec3 position, inout vec3 velocity) {
    /// interpolate position and velocity
    vec2 flooredfragCoord = floor(fragCoord);  // remove possible .5
    vec4 currentPositionAndMode = vec4(0.);
    vec4 rayVelocityAndMode = vec4(0.);
    float mode = 1.;
    if (
        flooredfragCoord.x <= 1. || flooredfragCoord.y <= 0. ||
        flooredfragCoord.x >= (iResolution.x-1.) || flooredfragCoord.y >= (iResolution.y-1.)
    ) {
        return 0.;  /// we are at border, so inteprpolation is not possible
    } else {
        bool is_velocity = (flooredfragCoord.x-2.*floor(flooredfragCoord.x/2.)) == 0.;
        if (is_velocity) {
            rayVelocityAndMode = texelFetch(iChannel2, ivec2(flooredfragCoord), 0);
            mode = rayVelocityAndMode.w;
            currentPositionAndMode = (
                texelFetch(iChannel2, ivec2(flooredfragCoord-vec2(1., 0.)), 0) * .5 +
                texelFetch(iChannel2, ivec2(flooredfragCoord+vec2(1., 0.)), 0) * .5
            );
        } else {
            currentPositionAndMode = texelFetch(iChannel2, ivec2(flooredfragCoord), 0);
            mode = currentPositionAndMode.w;
            rayVelocityAndMode = (
                texelFetch(iChannel2, ivec2(flooredfragCoord-vec2(1., 0.)), 0) * .5 +
                texelFetch(iChannel2, ivec2(flooredfragCoord+vec2(1., 0.)), 0) * .5
            );
        }
        /// if we reached black hole in any of them, return 0
        if ((rayVelocityAndMode.w + currentPositionAndMode.w) < 2.) {
            return 0.;
        }
    }
    position = currentPositionAndMode.xyz;
    velocity = rayVelocityAndMode.xyz;
    return mode;
}


