/*
 * Distance functions -> give us the distan—Åe to said object type
 * NEGATIVE return on the inside and POSITIVE on the outside
 * * vec3 point: origin point - the position you want your object to be at
 * * Object: obj to find distance to
 */

/* Get distance to the Box and put it in proper position */
float sdBox(vec3 point, Box box) {
    /// By default the scale is inversed

    point = abs(point) - vec3(box.wid, box.hig, box.dep);
    /// Account for inner position for there not to be distorted black dots
    return (length(max(point, 0.)) + min(max(point.x, max(point.y, point.z)), 0.));
}

float sdRoundBox( vec3 p, Box box, float r )
{
    vec3 d = abs(p) - vec3(box.wid, box.hig, box.dep);
    return min(max(d.x,max(d.y,d.z)), 0.0) + length(max(d,0.0)) - r;
}


/* Get distance to the Torus and put it in proper position */
float getDistTorus(vec3 point, Torus tor) {
    /// Projected distance to the outside layer of a torus
    float projDistToOut = length(point.xz) - tor.radBig;
    return length(vec2(projDistToOut, point.y)) - tor.radSmol;
}

/* Get distance to the Capsule */
float sdCapsule(vec3 point, Capsule cap) {
    vec3 edge = cap.bot - cap.top;
    vec3 distToCapsule = point - cap.top;

    float distToHit = dot(edge, distToCapsule) / dot(edge, edge);
    distToHit = clamp(distToHit, 0., 1.);

    vec3 pointHit = cap.top + distToHit*edge;
    return length(point - pointHit) - cap.rad;
}

/* Get distance to the Cylinder */
float sdCylinder(vec3 point, Cylinder cap) {
    vec3 edge = cap.bot - cap.top;
    vec3 distToSide = point - cap.top;

    float distToHit = dot(edge, distToSide) / dot(edge, edge);
    // distToHit = clamp(distToHit, 0., 1.);

    vec3 pointHit = cap.top + distToHit*edge;
    // X-axis dist to cylinder
    float xDist = length(point - pointHit) - cap.rad;
    float yDist = (abs(distToHit - .5) - .5) * length(edge);

    float exteriorDist = length(max(vec2(xDist, yDist), 0.));
    float interiorDist = min(max(xDist, yDist), 0.);
    return exteriorDist + interiorDist;
}

/* Get distance to the Sphere - The fastest! */
float sdSphere(vec3 point, Sphere sphere) {
    return length(point) - sphere.rad;
}

float sdSmoothUnion(float d1, float d2) {
    float k = 0.25;
    float h = max(k-abs(d1-d2),0.0);
    return min(d1, d2) - h*h*0.25/k;
}

// Yeah, right, this somehow works
float sdAsteroid(vec3 point, Sphere sphere, int astrIdx) {
    float boxDecreaseRad = 2.1 + float(astrIdx) * 0.1;

    Box b1 = Box(sphere.rad / boxDecreaseRad, sphere.rad / boxDecreaseRad, sphere.rad / boxDecreaseRad, vec3(0.0, 0.0, 0.0));

    vec3 boxPos = point - b1.pos;

    boxPos.xz *= Rotate(PI / 4.0);
    boxPos.yz *= Rotate(PI / 4.0);

    Box b2 = Box(sphere.rad / boxDecreaseRad, sphere.rad / boxDecreaseRad, sphere.rad / boxDecreaseRad, vec3(0.0, 0.0, 0.0));
    return sdSmoothUnion(sdRoundBox(point, b2, 0.4 + 0.5 * float(astrIdx) * 0.1),
        sdRoundBox(boxPos, b1, 0.4 + 0.5 * float(astrIdx) * 0.1));
}

/* Get distance to the Plane */
float sdPlane(vec3 point, vec3 plane) {
    return dot(point, normalize(plane));
}

/*
 * Get distance to the Piramid - for now used only in player creation
 * Code "borrowed" from https://www.shadertoy.com/view/Ntd3DX
 */
float sdPyramid(vec3 position, float halfWidth, float halfDepth, float halfHeight) {
    position.xz = abs(position.xz);

    // bottom
    float s1 = abs(position.y) - halfHeight;
    vec3 base = vec3(max(position.x - halfWidth, 0.0), abs(position.y + halfHeight), max(position.z - halfDepth, 0.0));
    float d1 = dot(base, base);

    vec3 q = position - vec3(halfWidth, -halfHeight, halfDepth);
    vec3 end = vec3(-halfWidth, 2.0 * halfHeight, -halfDepth);
    vec3 segment = q - end * clamp(dot(q, end) / dot(end, end), 0.0, 1.0);
    float d = dot(segment, segment);

    // side
    vec3 normal1 = vec3(end.y, -end.x, 0.0);
    float s2 = dot(q.xy, normal1.xy);
    float d2 = d;
    if (dot(q.xy, -end.xy) < 0.0 && dot(q, cross(normal1, end)) < 0.0) {
        d2 = s2 * s2 / dot(normal1.xy, normal1.xy);
    }
    // front/back
    vec3 normal2 = vec3(0.0, -end.z, end.y);
    float s3 = dot(q.yz, normal2.yz);
    float d3 = d;
    if (dot(q.yz, -end.yz) < 0.0 && dot(q, cross(normal2, -end)) < 0.0) {
        d3 = s3 * s3 / dot(normal2.yz, normal2.yz);
    }
    return sqrt(min(min(d1, d2), d3)) * sign(max(max(s1, s2), s3));
}


vec3 getRo(vec4 camera_props) {
    vec3 ro = vec3(-camera_props.x, camera_props.y, camera_props.z);
    ro.xz *= Rotate(PI);
    return ro;
}

// lookat - central point of the camera
// zoom - zoom  ==  distance from camera to the screen
// c - center point on the screen = ro + forward * zoom factor z
// ro = ray origin
// right - if we look straight from the camera on screen, it is x offset
// up - if we look straight from the camera on screen, it is y offset
// intersection - the point on the screen where ray passes through it
vec3 getRd(vec2 uv, vec3 ro, vec4 camera_props) {
    // variables to control camera if we need it
    vec3 lookat = vec3(camera_props.xy, 0.);
    float zoom = camera_props.w;

    vec3 forward = normalize(lookat - ro),
        right = normalize(cross(vec3(0, 1, 0), forward)),
        up = cross(forward, right),
        c = ro + forward * zoom,
        intersection = c + uv.x * right + uv.y * up,
        d = normalize(intersection - ro);
    return d;
}



vec3 blackHoleNullParticleAccl(vec3 p) {
    vec3 r = (p - BH_pos);
    return 10. * -1.5 * BH_R * r / pow(length(r), 5.0);
}

float heightprecompute(inout vec3 current_position, inout vec3 ray_velocity, float aim_height) {
    // mode is whether we reached height 1 (1.) or got below event horison (0.)
    // or did not converge (2.)
    float mode = 2.;
    float precompute_dt = dt / precompute_dt_divisor;
    for (int i = 0; i < MAX_STEPS_PRECOMPUTE * int(precompute_dt_divisor); ++i) {
        float dist = abs(current_position.z - aim_height);

        ray_velocity += blackHoleNullParticleAccl(current_position) * precompute_dt;

        vec3 position_delta = ray_velocity * precompute_dt;
        float dist_to_travel = length(position_delta);
        if (dist_to_travel > dist) {
            position_delta *= dist / dist_to_travel;
        }
        current_position += position_delta;

        // check that we got below event horizon
        if (distance(BH_pos, current_position) < BH_R) {
            mode = 0.;
            break;
        }

        // check that we landed on a plane
        if (abs(dist) < SURF_DIST) {
            ray_velocity = ray_velocity;
            mode = 1.;
            break;
        }
    }
    return mode;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    if (iFrame == 0) {
        vec2 uv = (fragCoord-.5*iResolution.xy) / iResolution.y;
        // TODO: I couldn't use the data channel for some reason.
        // texelFetch(iChannel0, ...) works if we #include <player.glsl>, but
        // then texelFetch(iChannel1, ...) doesn't work. Since camera position
        // is a constant currently, I just pasted it here for a temporary fix
        vec4 camera_props = vec4(0., 0., 20., 0.5);// texelFetch(iChannel0, ivec2(C_OPTIONS_COL, CAMERA_LAYER_ROW), 0);
        vec3 position = getRo(camera_props);
        vec3 velocity = getRd(uv, position, camera_props);
        float mode = heightprecompute(position, velocity, -1.);
        if (mode == 0.) {
            fragColor = vec4(0.);
        } else {
            /// put velocity if fragCoord.x is divisible by 2
            float flooredfragCoordx = floor(fragCoord.x);  // remove possible .5
            if ((flooredfragCoordx-2.*floor(flooredfragCoordx/2.)) == 0.) {
                fragColor = vec4(velocity, mode);
            } else {
                fragColor = vec4(position, mode);
            }
        }
    } else {
        fragColor = texelFetch(iChannel1, ivec2(fragCoord), 0);
    }
}

/// returns 0 when fetch is at border of black hole reached
float fetchPrecomputed(vec2 fragCoord, inout vec3 position, inout vec3 velocity) {
    /// interpolate position and velocity
    vec2 flooredfragCoord = floor(fragCoord);  // remove possible .5
    vec4 currentPositionAndMode = vec4(0.);
    vec4 rayVelocityAndMode = vec4(0.);
    float mode = 1.;
    if (
        flooredfragCoord.x <= 1. || flooredfragCoord.y <= 0. ||
        flooredfragCoord.x >= (iResolution.x-1.) || flooredfragCoord.y >= (iResolution.y-1.)
    ) {
        return 0.;  /// we are at border, so inteprpolation is not possible
    } else {
        bool is_velocity = (flooredfragCoord.x-2.*floor(flooredfragCoord.x/2.)) == 0.;
        if (is_velocity) {
            rayVelocityAndMode = texelFetch(iChannel1, ivec2(flooredfragCoord), 0);
            mode = rayVelocityAndMode.w;
            currentPositionAndMode = (
                texelFetch(iChannel1, ivec2(flooredfragCoord-vec2(1., 0.)), 0) * .5 +
                texelFetch(iChannel1, ivec2(flooredfragCoord+vec2(1., 0.)), 0) * .5
            );
        } else {
            currentPositionAndMode = texelFetch(iChannel1, ivec2(flooredfragCoord), 0);
            mode = currentPositionAndMode.w;
            rayVelocityAndMode = (
                texelFetch(iChannel1, ivec2(flooredfragCoord-vec2(1., 0.)), 0) * .5 +
                texelFetch(iChannel1, ivec2(flooredfragCoord+vec2(1., 0.)), 0) * .5
            );
        }
        /// if we reached black hole in any of them, return 0
        if ((rayVelocityAndMode.w + currentPositionAndMode.w) < 2.) {
            return 0.;
        }
    }
    position = currentPositionAndMode.xyz;
    velocity = rayVelocityAndMode.xyz;
    return mode;
}



void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    if (iFrame == 0) {
        vec2 uv = (fragCoord-.5*iResolution.xy) / iResolution.y;
        vec3 position = vec3(0.);
        vec3 velocity = vec3(0.);
        float mode = fetchPrecomputed(fragCoord, position, velocity);
        if (mode == 0.) {
            fragColor = vec4(0.);
        } else {
            mode = heightprecompute(position, velocity, 1.02);
            if (mode == 0.) {
                fragColor = vec4(0.);
            } else {
                mode = heightprecompute(position, velocity, 1.);
                if (mode == 0.) {
                    fragColor = vec4(0.);
                } else {
                    /// put velocity if fragCoord.x is divisible by 2
                    float flooredfragCoordx = floor(fragCoord.x);  // remove possible .5
                    if ((flooredfragCoordx-2.*floor(flooredfragCoordx/2.)) == 0.) {
                        fragColor = vec4(velocity, mode);
                    } else {
                        fragColor = vec4(position, mode);
                    }
                }
            }
        }
    } else {
        fragColor = texelFetch(iChannel0, ivec2(fragCoord), 0);
    }
}
