vec3 Pallete(float t)
{
    vec3 a = vec3(.5, .5, .5);
    vec3 b = vec3(.5, .5, .5);
    vec3 c = vec3(1.0, 1.0, 1.0);
    vec3 d = vec3(0.26, .42, .56);

    return a + b * cos(3.14159 * 2.0 * (c * t + d));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord / iResolution.xy - 0.5) * 2.0 * vec2(iResolution.x / iResolution.y, 1.0);
    vec2 uv0 = uv;

    vec3 finalCol = vec3(0.0);
    for(int i = 0; i < 5; i++)
    {
        uv = fract(uv * 1.2) - .5;

        float d = length(uv) * exp(-length(uv0));

        vec3 col = Pallete(length(uv0) + iTime * .6);

        d = sin(d * 8.0 + iTime) / 8.0;
        d = abs(d);

        d = pow(0.005 /d, 1.2);

        // d = smoothstep(0.0, 0.1, d);

        finalCol += d * col;
    }

    // Output to screen
    fragColor = vec4(finalCol, 1.0);
}
