vec4 bw(vec4 i)
{
    //return vec4(i.g);
    float c = 0.299*i.r + 0.587*i.g + 0.114*i.b;
    return vec4(c, c, c, 1);
}
vec4 threshold(float T, vec4 col)
{
    float c = bw(col).x;
    if(c > T)
    {
        return vec4(1.0);
    }
    return vec4(0.0);
}
