// reproducing Erlend Robaye's https://www.facebook.com/groups/procgenart/permalink/1991283661002453/

#define AA 1    // antialiasing
#define N  10   // NxN subsamples

// --- complex op from https://shadertoyunofficial.wordpress.com/2019/01/02/programming-tricks-in-shadertoy-glsl/

#define CS(a)        vec2( cos(a), sin(a) )
//#define cart2pol(U)  vec2( length(U), atan((U).y,(U).x) )
#define pol2cart(U) ( (U).x * CS( (U).y ) )
#define cmod(Z)      length(Z)
#define carg(Z)      atan( (Z).y, (Z).x )
#define cmul(A,B)  ( mat2( A, -(A).y, (A).x ) * (B) )  // by deMoivre formula
#define cinv(Z)    ( vec2( (Z).x, -(Z).y ) / dot(Z,Z) ) 
#define cdiv(A,B)    cmul( A, cinv(B) )
//#define cpow(Z,v)    pol2cart( vec2( pow(cmod(Z),v) , (v) * carg(Z) ) )
#define cpow(A,B)    cexp( cmul( B, clog(A) ) )
#define cexp(Z)      pol2cart( vec2( exp((Z).x), (Z).y ) )
#define clog(Z)      vec2( log(cmod(Z)), carg(Z) )
#define imul(Z)      vec2(-(Z).y,(Z).x)
#define csin(Z)    ( -.5* imul( cexp(imul(Z)) - cexp(-imul(Z)) ) )

#define VIV_DEBUG 1
#define VIV_YES 1
#define VIV_NOT 0
#define VIV_ASSERT(x) if (!(x)) { O = vec4(1.0, 0.0, 0.0, 1.0); return; }

vec4 judge(bool res) {
    vec4 O;
    if (res) {
        O = vec4(1.0, 1.0, 0.0, 1.0);
    } else {
        O = vec4(0.0, 1.0, 1.0, 1.0);
    }
    return O;
}

bool isNAN(float x) {
    if (x >= 0.0) return false;
    if (x < 0.0) return false;
    return true;
}

void mainImage( out vec4 O, vec2 u )
{
    vec2 R  = iResolution.xy, z;
#if VIV_DEBUG
    R.x = 512.0;
    R.y = 288.0;
#endif
    float s = 1.8 / R.y;                // scale
    z = ( (u-(R-R.y)/2.) * s + vec2(-.2,2.2) ) ; // x = -0.2..1.65  y = 2.2 .. 4
    O = vec4(0);  

#if !VIV_DEBUG
    if ( z.x < -.2 || z.x > 1.65 ) return;
#endif

#if VIV_DEBUG
    z.x = -0.195;
    z.y = 2.25;
    float INF = exp(89.0);
#endif

#if VIV_NOT
    if (exp(89.0) >= exp(90.0)) {
        O = vec4(1.0, 1.0, 0.0, 1.0);
        return;
    } else {
        O = vec4(0.0, 1.0, 1.0, 1.0);
        return;
    }
#endif

#if VIV_DEBUG
    vec2 zz = z;
#endif

#if AA

#if VIV_DEBUG
  //for( int k=12; k<13; k++) {
  int k = 40;
  {
#else
  for( int k=0; k<N*N; k++) {
#endif

#if VIV_DEBUG
    vec2 z = zz + (vec2(k%N,k/N)-.5*float((N+1)/2)) / float(N) * s;
#else
    vec2 z = z + (vec2(k%N,k/N)-.5*float((N+1)/2)) / float(N) * s;
#endif


#if VIV_NOT
    VIV_ASSERT(k == 12);
    VIV_ASSERT(z.y >= 2.249062657);
    VIV_ASSERT(z.y <  2.249062658);
    //O = judge(z.y >= 2.249062657);
    //return;
#endif

#if VIV_DEBUG
    VIV_ASSERT(k == 40);
    VIV_ASSERT(z.y >= 2.2509375819);
#endif

#endif

    for (int i=0; i<4; i++) 
        z = cdiv( cexp(z) , z );          // 4x z = (e^z)/z


#if VIV_DEBUG
    VIV_ASSERT(k == 40);
    VIV_ASSERT(z.y >= 1.410468);
#endif

    z = cdiv( csin(z) , z-vec2(1,0) );    //    z = sin(z) / (z-1)



    for (int i=0; i<4; i++)                                      // should simplifies to
        z = cpow( z, vec2(1,6) );         // 4x z = z^(1+6i)     //   z^(1+6i)â´


#if VIV_NOT
    VIV_ASSERT(k == 40);
    O = judge(z.y >= -1073.97);
    return;
#endif

#if 0
    if ( cmod(z) < .1 ) O++;              // could do this intead of following (pretty close)
#else
    z = cdiv( cexp(z) , z );              //    z = (e^z)/z      // should simplifies to

#if VIV_DEBUG
    if (z.y >= INF) {
        O = vec4(0.0, 0.0, 1.0, 1.0);
        return;
    }
#endif


    z = clog( z );                        //    z = log(z)       //   z -= log(z)
 // if ( cmod(z) < exp(45.) &&   cmod(z) > exp(-45. )) O++;      // could do this intead of clog

 // O += vec4(!isnan(cmod(z)) && !isinf(cmod(z)));  // white if defined, otherwise black
    if (cmod(z) < 45. ) O++;                        // simplifies to this
#endif

#if AA
  }
#if VIV_DEBUG
  if (O.x >= 50.0) {
     O = vec4(1.0, 0.0, 0.0, 1.0);
  } else {
     O = vec4(0.0, 1.0, 0.0, 1.0);
  }
#endif

#endif

#if !VIV_DEBUG
  O /= float(N*N);                        // average subpixels
#endif
}
