#define PI 3.14159265
#define PI2 2.0*PI

float gen( float fT, float fPow, float fAmp, float fTMul )
{
    fT *= fTMul;
    float f = 0.0;
    f += sin( fT * 1.0 + 4.1 ) * 0.1f;
    f += sin( fT * 1.6 + 4.3 ) * 0.2f;
    f += sin( fT * 2.7 + 7.7 ) * 0.1f;
    f += sin( fT * 3.1 + 1.4 ) * 0.1f;
    f += sin( fT * 4.8 + 4.8 ) * 0.2f;
    f += sin( fT * 6.3 + 7.1 ) * 0.1f;
    f += sin( fT * 7.0 + 8.4 ) * 0.2f;

    f = f*0.5+0.5;

    f = pow( f, fPow );

    f = f*2.0-1.0;

    float fSign = sign( f );
    f = ( 1.0 - exp( -abs( f ) * fAmp ) ) * fSign;
    f /= 1.0 - exp( -1.0*fAmp );


    f = f*0.5+0.5;

    return f;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    float time = iTime+fragCoord.x*0.00001;

    float fStart = 1.0 - exp( -time*0.1 );

    float f1 = (sin( time*5.0 )*0.5+0.5)*0.02+0.98;
    float f2 = (sin( time*7.0 )*0.5+0.5)*0.02+0.98;
    //vec2 vMouse = iMouse.xy/iResolution.xy;
    vec2 vSignal = vec2( 0.0, 0.0 );
    vSignal.x = gen( time    , 1.3, 0.9, 300.0*f1 ) + gen( time    , 0.3, 0.9, 1700.0*f2 );
    vSignal.y = gen( time+5.0, 1.3, 0.9, 250.0*f1 ) + gen( time    , 0.3, 0.9, 1800.0*f2 );
    vSignal *= gen( time     , 1.0, 1.2, 40.0 ) * gen( time     , 1.0, 1.2, 140.0 );
    vSignal *= fStart;
    fragColor = vec4( vSignal.x, vSignal.y, 0.0, 1.0 );
}

