// Fork of "Strange Wave" by bitless. https://shadertoy.com/view/NdK3R3
// 2022-12-02 20:34:36

// Author: bitless
// Title: Strange Wave

// Thanks to Patricio Gonzalez Vivo & Jen Lowe for "The Book of Shaders"
// and Fabrice Neyret (FabriceNeyret2) for https://shadertoyunofficial.wordpress.com/
// and Inigo Quilez (iq) for  https://iquilezles.org/www/index.htm
// and whole Shadertoy community for inspiration.

#define r( n ) fract(sin(n)*43758.5453) //random
#define n(v) mix(r(floor(v)),r(floor(v)+1.),smoothstep(0.,1.,fract(v))) 
#define h(v) ( .6 + .6 * cos(6.3*(v) + vec4(0,23,21,0) ) ) //hue

float myTime;

void w(vec2 u, float s, inout vec4 C)
{
    float   t = n(u.x)*n(u.x-s-myTime)+.4 //top edge
            ,b = n(u.x+5.)*n(u.x-9.-s-myTime)-.8; //bottom edge

    C = mix(C, 
            h(s*.2+myTime*.3+u.x*.1), 
            pow(abs((u.y*0.4-b*1.1)/(t-b)-.5)*1.9,19.)+.15)*smoothstep(0.,115./iResolution.y,myTime*(t-u.y)*(u.y-b*0.9));
}

void mainImage( out vec4 C, in vec2 g)
{
    vec2 r = iResolution.xy
        ,u = (g+g-r)/r.y;

    vec2 mouse = (iMouse.xy == vec2(0.)) ? vec2(1.0) : iMouse.xy/iResolution.xy;

    myTime = cos(iTime)*sin(iTime)* 3.4 * mouse.x/mouse.y;
    u.x-=myTime/(5.*mouse.x);
    for (float s = 1.5; s > 0.; s-=.1) {
        w(u*vec2(4.,0.4+s),-s*0.4,C);
        if(s>0.2) {
            C.x = clamp(C.x,0.0,0.9/s+fract(iTime/71.0));
            C.y = clamp(C.y,0.0,0.9/s+fract(iTime/91.0));
            C.z = clamp(C.z,0.0,0.9/s+fract(iTime/93.0));
        }
     }
}
