const float MAX = 128.0;
float drawMandelbrot(vec2 uv){
    vec2 c = 2.0 * uv - vec2(0.4, 0.0);
    vec2 z = vec2(0.0);
    float iter = 0.0;

    for(float i; i < MAX; i++){
        z = vec2(z.x * z.x - z.y * z.y,
                 2.0 * z.x * z.y) + c;
        if(dot(z,z) > 5.0) return iter / MAX;
        iter++;
    }

    return 1.;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ( fragCoord -.5*iResolution.xy )/iResolution.y;
    vec3 col = vec3(0.0);

    float m = drawMandelbrot(uv);
    col += m;

    fragColor = vec4(col,1.0);
}
