void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;

    vec3 ro = vec3(0.0, 0.0, -1.);
    vec3 rd = vec3(uv, 0.0) - ro;

    vec3 col;

    for(float i = 0.0; i < 4.0; i++){
        vec3 PointR = vec3(0.0, i * cos(iTime), 0.5);
        float R = length(cross(PointR - ro, rd)) / length(rd);

        vec3 PointV = vec3(i * cos(iTime - 1.0), 0.0, 0.5);
        float V = length(cross(PointV - ro, rd)) / length(rd);

        vec3 PointB = vec3(i * cos(iTime - 2.0), 1.0 * cos(iTime - 2.0), 0.5);
        float B = length(cross(PointB - ro, rd)) / length(rd);

        col = vec3(R, V, B);

        vec3 PointR1 = vec3(0.0, 1.0 * sin(iTime / 2.), 0.5);
        float R1 = length(cross(PointR1 - ro, rd)) / length(rd);

        vec3 PointV1 = vec3(1.0 * sin((iTime - 1.0) / 2.), 0.0, 0.5);
        float V1 = length(cross(PointV1 - ro, rd)) / length(rd);

        vec3 PointB1 = vec3(1.0 * sin((iTime - 2.0) / 2.), 1.0 * sin(iTime - 2.0), 0.5);
        float B1 = length(cross(PointB1 - ro, rd)) / length(rd);

        col *= vec3(R1, V1, B1);
    }

    fragColor = vec4(1.-exp(-col), 0.0);
}
